      const { data } = await axios.post(
        "/api/user",
        {
          walletAddr,
          walletAddr,
          password,
          pic,
        },
        config
      );
      console.log(data);
      toast({
        title: "Registration Successful",
        status: "success",
        duration: 5000,
        isClosable: true,
        position: "bottom",
